mapper.select:
SELECT a.code AS code, a.nickname AS nickname, e.level AS level, c.status AS status, c.login_date AS login_date, d.content AS reason FROM member AS a JOIN info_detail AS b ON a.code = b.code JOIN service_log AS c ON a.code = c.code JOIN reason AS d ON c.reason = d.num JOIN grade AS e ON b.acc_money BETWEEN e.lowest AND e.highest WHERE 1=1
AND a.id=#{id}
AND a.passwd=#{passwd}
